INSERT INTO `gossip_menu_option` VALUES ('7768', '0', '0', 'Hello, Greatmother. Garrosh told me that you wanted to speak with me.', '1', '1', '7767', '0', '0', '0', '0', null, '962');
INSERT INTO `gossip_menu_option` VALUES ('7768', '1', '0', 'Garrosh is beyond redemption, Greatmother. I fear that in helping the Mag\'har, I have convinced Garrosh that he is unfit to lead.', '1', '1', '7974', '0', '0', '0', '0', null, '963');
INSERT INTO `gossip_menu_option` VALUES ('7767', '0', '0', 'You raised all of the orcs here, Greatmother?', '1', '1', '7766', '0', '0', '0', '0', null, '0');
