INSERT INTO `gossip_menu_option` VALUES ('7765', '0', '0', 'What can be done? I have tried many different things. I have done my best to help the people of Nagrand. Each time I have approached Garrosh, he has dismissed me.', '1', '1', '7764', '0', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('7764', '0', '0', 'Left? How can you choose to leave?', '1', '1', '7763', '0', '0', '0', '0', null, '0');
