INSERT INTO `gossip_menu_option` VALUES ('7535', '0', '0', 'Sironas is an eredar... I mean, yes, obviously.', '1', '1', '7534', '0', '0', '0', '0', '', '0');
INSERT INTO `gossip_menu_option` VALUES ('7534', '0', '0', 'The Vector Coil is massive. I hope we have more than one abomination guarding the numerous weak points.', '1', '1', '7533', '0', '0', '0', '0', '', '0');
INSERT INTO `gossip_menu_option` VALUES ('7531', '0', '0', 'I\'m a prisoner, what does it look like? The draenei filth captured me as I exited the sun gate. They killed our portal controllers and destroyed the gate. The Sun King will be most displeased with this turn of events.', '1', '1', '7537', '0', '0', '0', '0', '', '1147');
INSERT INTO `gossip_menu_option` VALUES ('7537', '0', '0', 'Ah yes, Sironas. I had nearly forgotten that Sironas was here. I served under Sironas back on Outland. I hadn\'t heard of this abomination, though; those damnable draenei captured me before I even fully materialized on this world.', '1', '1', '7536', '0', '0', '0', '0', '', '0');
